class SELECTIVE_AGGR(t.nn.Module):
    def __init__(self, tg_agg, family="dit", arith="mean"):
        assert family in ["dit", "sdv15", "sdxl", "alpha", "sigma", "hunyuan"]
        assert arith in ["mean", "sum"]
        super(SELECTIVE_AGGR, self).__init__()

        self.tg_aggr = tg_agg
        if arith == "mean":
            self.method = t.mean
        else:
            self.method = t.sum
        if family in ["dit", "ditOld"]:
            self.num_nodes = NUM_DIT_NODES
            self.idx_dict = DIT_HOP_DICT
        elif family in ["sdxl", "sdxlOld"]:
            self.num_nodes = NUM_SDXL_NODES
            self.idx_dict = SDXL_HOP_DICT
        elif family in ["alpha", "sigma"]:
            self.num_nodes = NUM_PIXART_NODES
            self.idx_dict = PIXART_HOP_DICT
        elif family in ['hunyuan']:
            self.num_nodes = NUM_HUNYUAN_NODES
            self.idx_dict = HUNYUAN_HOP_DICT
        else:
            self.num_nodes = NUM_SDV15_NODES
            self.idx_dict = SDV15_HOP_DICT

    def forward(self, h, hop, batch):
        if self.idx_dict[hop] is None:
            return self.tg_aggr(h, batch)
        assert h.shape[0] % self.num_nodes == 0, h.shape[0]
        assert hop in self.idx_dict.keys()
        batch_size = h.shape[0] // self.num_nodes
        h = h.view(batch_size, self.num_nodes, h.shape[-1])
        h = h[:, self.idx_dict[hop], :]
        return self.method(h, dim=1, keepdim=False)